mapper.select:
select count(0)
  from DEP_ETL_JOB_RECORDERS


          and JOB_NAME =#{jobName,jdbcType=VARCHAR}


          and JOB_TYPE = #{jobType,jdbcType=NUMERIC}


          and START_TIME  > #{startTime,jdbcType=TIMESTAMP}


          and END_TIME < #{endTime,jdbcType=TIMESTAMP}


          and JOB_STATUS = #{jobStatus,jdbcType=NUMERIC}

SELECT ROWNUM, TT.* FROM (SELECT T.JOB_ID, T.JOB_TYPE, (
SELECT COUNT(0) FROM DEP_ETL_JOB_RECORDERS DEJR WHERE DEJR.JOB_ID = T.JOB_ID AND DEJR.JOB_STATUS = '1'
) AS SUCCESS_NUMBER, (
SELECT COUNT(0) FROM DEP_ETL_JOB_RECORDERS DEJR WHERE DEJR.JOB_ID = T.JOB_ID AND DEJR.JOB_STATUS = '2'
) AS FAIL_NUMBER,(
SELECT MAX(DEJR.START_TIME) FROM DEP_ETL_JOB_RECORDERS DEJR WHERE DEJR.JOB_ID = T.JOB_ID
) AS MAX_TIME, T.JOB_NAME FROM DEP_ETL_JOB_RECORDERS T
and T.JOB_ID =#{jobId,jdbcType=VARCHAR}
and T.JOB_TYPE = #{jobType,jdbcType=NUMERIC}
and T.START_TIME > #{startTime,jdbcType=TIMESTAMP}
and T.END_TIME < #{endTime,jdbcType=TIMESTAMP}
and T.JOB_STATUS = #{jobStatus,jdbcType=NUMERIC}
GROUP BY T.JOB_ID, T.JOB_NAME, T.JOB_TYPE) TT
order by tt.JOB_ID

select
  ,JOB_RESULT_INFO
  from DEP_ETL_JOB_RECORDERS


          and JOB_NAME =#{jobName,jdbcType=VARCHAR}


          and JOB_TYPE = #{jobType,jdbcType=NUMERIC}


          and START_TIME  > #{startTime,jdbcType=TIMESTAMP}


          and END_TIME < #{endTime,jdbcType=TIMESTAMP}


          and  JOB_STATUS = #{jobStatus,jdbcType=NUMERIC}


          and  JOB_ID = #{jobId,jdbcType=VARCHAR}
